package com.cliente.crud.controller;import com.cliente.crud.model.ClienteEntity;import com.cliente.crud.model.RespuestaGenerica;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.http.MediaType;import org.springframework.stereotype.Controller;import org.springframework.web.bind.annotation.*;import com.cliente.crud.service.ClienteService;@Controller@RestController@RequestMapping("/api/v1/prueba")public class ClienteController {    @Autowired    ClienteService clienteService;    @GetMapping(value = "/cliente/listar")    public @ResponseBody RespuestaGenerica listarCliente() {        return clienteService.listarCliente();    }    @GetMapping(value = "/cliente/listarById")    public @ResponseBody RespuestaGenerica listarClienteById(@RequestParam Integer idCliente) {        return clienteService.listarClienteById(idCliente);    }    @PostMapping(value = "/cliente/guardar", produces = MediaType.APPLICATION_JSON_VALUE)    public @ResponseBody RespuestaGenerica guardarCliente(@RequestBody ClienteEntity cliente ) {        return clienteService.guardarCliente(cliente);    }    @PutMapping(value = "/cliente/actualizar", produces = MediaType.APPLICATION_JSON_VALUE)    public @ResponseBody RespuestaGenerica modificarCliente(@RequestBody ClienteEntity cliente) {        return clienteService.modificarCliente(cliente);    }    @DeleteMapping(value = "/cliente/eliminar", produces = MediaType.APPLICATION_JSON_VALUE)    public @ResponseBody RespuestaGenerica eliminarCliente(@RequestParam Integer idCliente) {        return clienteService.eliminarCliente(idCliente);    }}