package com.cliente.crud.service.impl;import com.cliente.crud.dao.ClienteDAO;import com.cliente.crud.dto.EstatusResponse;import com.cliente.crud.model.ClienteEntity;import com.cliente.crud.service.ClienteService;import com.cliente.crud.model.RespuestaGenerica;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;import java.util.List;import java.util.Optional;@Service("clienteService")public class ClienteServiceImpl implements ClienteService {    @Autowired    ClienteDAO clienteDAO;    private int OK = EstatusResponse.OK.getEstatus();    private String msjOK = "Servicio ejecutado correctamente";    @Override    public RespuestaGenerica listarCliente() {       List<ClienteEntity> cliente = clienteDAO.obtenerClientes();        return new RespuestaGenerica(OK, msjOK, "exito", cliente);    }    @Override    public RespuestaGenerica listarClienteById(Integer idCliente) {        Optional<ClienteEntity> cliente = clienteDAO.obtenerClientesById(idCliente);        return new RespuestaGenerica(OK, msjOK, "exito", cliente);    }    @Override    public RespuestaGenerica guardarCliente(ClienteEntity cliente) {        ClienteEntity clienteRegistro = clienteDAO.registarCliente(cliente);        return new RespuestaGenerica(OK, msjOK, "exito", clienteRegistro);    }    @Override    public RespuestaGenerica modificarCliente(ClienteEntity cliente) {        ClienteEntity clienteActualizar = clienteDAO.obtenerClientesById(cliente.getIdCliente()).get();        clienteActualizar.setNombreCliente(cliente.getNombreCliente());        clienteActualizar.setEmail(cliente.getEmail());        ClienteEntity clienteActualizado = clienteDAO.registarCliente(clienteActualizar);        return new RespuestaGenerica(OK, msjOK, "exito", clienteActualizado);    }    @Override    public RespuestaGenerica eliminarCliente(Integer idCliente) {        String eliminacionCliente = clienteDAO.eliminarCliente(idCliente);        return new RespuestaGenerica(OK, msjOK, "exito", eliminacionCliente);    }}